class Group < ActiveRecord::Base
  has_one :bill
  has_many :posts
  
  acts_as_followable
  
  def self.find_by_bill_serial(serial)
    bill = Bill.find_by_serial(serial)
    return nil if bill.blank?
    @group = Group.find(bill.group_id)
  end
    
